    <script type="text/javascript">
        // SLIDE BANNER
        // ADD DATA
        $(document).on('click', '.slideBannerAdd-modal', function() {
            $('.modal-title').text('Add Data');
            $('#slideBannerAdd').modal('show');
        });

        // EDIT DATA
        $(document).on('click', '#editSlideBanner', function (event) {
            event.preventDefault();
            var id = $(this).data('id');
            console.log(id)
            $.get('slideBanner/edit/' + id, function (data) {
                $('.modal-title').text('Update Data');
                $('#slideBannerEdit').modal('show');
                $('#id').val(data.data.id);
                $('#image').val(data.data.image);
                $('#status_edit').val(data.data.status);
            })
        });

        // DELETE DATA
        $(".deleteSlideBanner").click(function(e) {
            e.preventDefault();
            var id = $(this).data("id");

            $.ajaxSetup({
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                }
            });

            if (confirm("Are you sure you want to Delete this data?")) {
                $.ajax({
                    url: "<?php echo e(url('/slideBanner/delete')); ?>/" + id,
                    type: 'DELETE',
                    data: {
                        "id": id,
                    },
                    success: function () {
                        alert('You have successfully delete your files');
                        window.location.href = "<?php echo e(url('/slideBanner')); ?>";
                    },
                    error: function() {
                        alert('Failed!');
                    }
                });
            } else {
                return false;
            }
        });


